mapper.select:
select  
	*
from v_stock_price_d where stock_id=#{stockId};

select td.stock_name, td.stock_id, td.stock_price, td.price_change, td.price_change_percent, td.trading_volumn, td.trading_amount, date_format(td.list_date,'%Y-%c-%d') list_date, hand_volumn, total_amount from oltp.v_stock_info td left join t_rl_snap_customer_favor tf on tf.favor_object=td.stock_id where tf.customer_id=#{customerId} and tf.favor_type=#{favorType}
and td.trans_insttt = 1
and td.protocol_insttt = 1
order by tf.create_date desc limit #{currentRecord},#{pageSize};

select stock_name, stock_id, stock_price, price_change, price_change_percent, trading_volumn, trading_amount, hand_volumn, total_amount, trading_amount from v_stock_info where 1=1
and trans_insttt = 1
and protocol_insttt = 1
and (area is null or area = '')
and area = #{area}
and industry_name = #{industryName}
limit #{currentRecord},#{pageSize};

select inf.stock_name, inf.stock_id, inf.stock_price, inf.price_change, inf.price_change_percent, inf.trading_volumn from t_rl_snap_maker_broker_full idx left join v_stock_info inf on idx.stock_id = inf.stock_id where idx.maker_broker = #{brokerName}
select inf.stock_name, inf.stock_id, inf.stock_price, inf.price_change, inf.price_change_percent, inf.trading_volumn from olap.t_orgn_etl_broker idx left join v_stock_info inf on idx.stock_id = inf.stock_id where idx.broker_name like concat(#{brokerName},'%') and idx.snapshot_day = date_format(sysdate(),'%Y%m%d')
limit #{currentRecord},#{pageSize};

select media_no,
	media_desc media_name,
	media_location,
	media_format media_type,
	date_format(create_date,'%Y-%m-%d') create_date
from t_rl_trs_media 
	where media_author = #{mediaAuthor} 

		and object_id=#{objectId} 

	order by create_date desc 
	 limit #{currentRecord},#{pageSize};

select t1.stock_id, t1.stock_name, t1.stock_price, t2.cnt tradingVolumn from v_stock_info t1 left join (select tcp.object_no,count(1) cnt from oltp.t_rl_trs_cust_sign_project tcp group by tcp.object_no)t2 on t1.stock_id = t2.object_no
where t1.stock_id = #{stockLabel} or t1.stock_name=#{stockLabel}
order by t1.list_date desc
order by t2.cnt desc
limit #{currentRecord},#{pageSize};
